import { useState, useEffect, useRef } from "react";

interface ProductCarouselProps {
  images: string[];
  productName?: string;
}

export default function ProductCarousel({ images, productName = "4Dreams" }: ProductCarouselProps) {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const carouselRef = useRef<HTMLDivElement>(null);
  const startX = useRef<number | null>(null);
  const currentX = useRef<number | null>(null);
  const isDragging = useRef(false);
  const startScrollLeft = useRef(0);

  const handleThumbnailClick = (index: number) => {
    if (isTransitioning) return;
    setActiveIndex(index);
    scrollToIndex(index);
  };

  const scrollToIndex = (index: number) => {
    const carousel = carouselRef.current;
    if (carousel) {
      setIsTransitioning(true);
      const imageWidth = carousel.children[0]?.clientWidth || 0;
      carousel.scrollTo({
        left: imageWidth * index,
        behavior: 'smooth'
      });
      setTimeout(() => setIsTransitioning(false), 300);
    }
  };

  const handleDotClick = (index: number) => {
    if (isTransitioning) return;
    setActiveIndex(index);
    scrollToIndex(index);
  };

  // Touch/Mouse event handlers
  const handleStart = (clientX: number) => {
    if (isTransitioning) return;
    startX.current = clientX;
    currentX.current = clientX;
    isDragging.current = true;
    startScrollLeft.current = carouselRef.current?.scrollLeft || 0;
  };

  const handleMove = (clientX: number) => {
    if (!isDragging.current || !startX.current || isTransitioning) return;

    currentX.current = clientX;
    const deltaX = startX.current - clientX;
    const carousel = carouselRef.current;

    if (carousel) {
      carousel.scrollLeft = startScrollLeft.current + deltaX;
    }
  };

  const handleEnd = () => {
    if (!isDragging.current || !startX.current || !currentX.current || isTransitioning) return;

    const deltaX = startX.current - currentX.current;
    const threshold = 50; // minimum swipe distance
    const carousel = carouselRef.current;

    if (carousel && Math.abs(deltaX) > threshold) {
      const imageWidth = carousel.children[0]?.clientWidth || 0;
      let newIndex = activeIndex;

      if (deltaX > 0 && activeIndex < images.length - 1) {
        // Swipe left - next image
        newIndex = activeIndex + 1;
      } else if (deltaX < 0 && activeIndex > 0) {
        // Swipe right - previous image
        newIndex = activeIndex - 1;
      }

      setActiveIndex(newIndex);
      scrollToIndex(newIndex);
    } else if (carousel) {
      // Snap back to current image
      scrollToIndex(activeIndex);
    }

    isDragging.current = false;
    startX.current = null;
    currentX.current = null;
  };

  // Mouse events
  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    handleStart(e.clientX);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    handleMove(e.clientX);
  };

  const handleMouseUp = () => {
    handleEnd();
  };

  // Touch events
  const handleTouchStart = (e: React.TouchEvent) => {
    handleStart(e.touches[0].clientX);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    handleMove(e.touches[0].clientX);
  };

  const handleTouchEnd = () => {
    handleEnd();
  };

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    const handleScroll = () => {
      if (isDragging.current || isTransitioning) return;
      const imageWidth = carousel.children[0]?.clientWidth || 0;
      const currentIndex = Math.round(carousel.scrollLeft / imageWidth);
      setActiveIndex(currentIndex);
    };

    carousel.addEventListener('scroll', handleScroll);
    return () => carousel.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="px-4 mb-6">
      <div className="relative mb-4">
        {/* Main Carousel with Swipe Support */}
        <div 
          ref={carouselRef}
          className="carousel-container flex overflow-x-auto space-x-2 mb-3 cursor-grab active:cursor-grabbing" 
          style={{ scrollbarWidth: 'none', msOverflowStyle: 'none' }}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
          data-testid="swipe-carousel"
        >
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`${productName} - Vista ${index + 1}`}
              className="w-full h-96 object-contain rounded-lg flex-shrink-0 select-none"
              data-testid={`img-carousel-${index}`}
              draggable={false}
            />
          ))}
        </div>

        {/* Dot Indicators */}
        <div className="flex justify-center space-x-2 mb-3">
          {images.map((_, index) => (
            <button
              key={index}
              onClick={() => handleDotClick(index)}
              className={`w-2 h-2 rounded-full transition-all duration-200 ${
                activeIndex === index 
                  ? 'bg-gray-800 dark:bg-white' 
                  : 'bg-gray-300 dark:bg-gray-600'
              }`}
              data-testid={`dot-indicator-${index}`}
              aria-label={`Ir para imagem ${index + 1}`}
            />
          ))}
        </div>
      </div>

      {/* Thumbnail Navigation */}
      <div className="flex space-x-2 overflow-x-auto">
        {images.map((image, index) => (
          <button
            key={index}
            className={`border-2 ${activeIndex === index ? 'border-primary' : 'border-transparent'} rounded-lg overflow-hidden flex-shrink-0`}
            onClick={() => handleThumbnailClick(index)}
            data-testid={`button-thumbnail-${index}`}
          >
            <img
              src={image.replace('800-auto', '150-auto')}
              alt={`Thumbnail ${index + 1}`}
              className="w-16 h-16 object-cover"
            />
          </button>
        ))}
      </div>
    </div>
  );
}

client/src/components/ProductInfo.tsx.content:
import { useState } from "react";
import { Star, StarHalf, ShoppingCart } from "lucide-react";
import { useCart } from "@/hooks/use-cart";
import { useToast } from "@/hooks/use-toast";

interface ProductInfoProps {
  productId: string;
  name: string;
  subtitle: string;
  rating: number;
  reviewCount: number;
  originalPrice: number;
  currentPrice: number;
  installments: string;
}

export default function ProductInfo({
  productId,
  name,
  subtitle,
  rating,
  reviewCount,
  originalPrice,
  currentPrice,
  installments
}: ProductInfoProps) {
  const [quantity, setQuantity] = useState(1);
  const [isAdding, setIsAdding] = useState(false);
  const { addToCart, openCart } = useCart();
  const { toast } = useToast();

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<Star key={i} className="w-4 h-4 star-filled fill-current" />);
    }

    if (hasHalfStar) {
      stars.push(<StarHalf key="half" className="w-4 h-4 star-filled fill-current" />);
    }

    const remainingStars = 5 - Math.ceil(rating);
    for (let i = 0; i < remainingStars; i++) {
      stars.push(<Star key={`empty-${i}`} className="w-4 h-4 star-empty" />);
    }

    return stars;
  };

  const increaseQuantity = () => {
    setQuantity(prev => prev + 1);
  };

  const decreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(prev => prev - 1);
    }
  };

  const handleAddToCart = async () => {
    setIsAdding(true);
    try {
      await addToCart(productId, quantity);
      toast({
        title: "Produto adicionado ao carrinho!",
        description: `${quantity}x ${name} foram adicionados ao seu carrinho.`,
        duration: 3000,
      });
      openCart(); // Abrir o carrinho após adicionar
    } catch (error) {
      toast({
        title: "Erro ao adicionar ao carrinho",
        description: "Tente novamente em instantes.",
        variant: "destructive",
        duration: 3000,
      });
    } finally {
      setIsAdding(false);
    }
  };

  return (
    <div className="px-4 mb-6">
      <h1 className="text-2xl font-bold text-foreground mb-2" data-testid="text-product-title">{name}</h1>
      <p className="text-muted-foreground mb-3" data-testid="text-product-subtitle">{subtitle}</p>

      {/* Rating */}
      <div className="flex items-center mb-4">
        <div className="flex items-center mr-2" data-testid="rating-stars">
          {renderStars(rating)}
        </div>
        <span className="text-sm font-medium mr-1" data-testid="text-rating">{rating} de 5</span>
        <span className="text-sm text-muted-foreground" data-testid="text-review-count">({reviewCount})</span>
      </div>

      {/* Pricing */}
      <div className="mb-6">
        <div className="text-lg text-muted-foreground line-through mb-1" data-testid="text-original-price">
          R$ {originalPrice.toFixed(2).replace('.', ',')}
        </div>
        <div className="text-3xl font-bold text-foreground mb-1" data-testid="text-current-price">
          R$ {currentPrice.toFixed(2).replace('.', ',')}
        </div>
        <div className="text-sm text-muted-foreground" data-testid="text-installments">{installments}</div>
      </div>

      {/* Quantity and Buy Button */}
      <div className="mb-6">
        <div className="mb-4">
          <label className="block text-sm font-medium mb-2" data-testid="label-quantity">Quantidade</label>
          <div className="flex items-center space-x-0">
            <button 
              className="quantity-button rounded-l-md" 
              onClick={decreaseQuantity}
              data-testid="button-decrease-quantity"
            >
              −
            </button>
            <input 
              type="number" 
              value={quantity} 
              min="1" 
              className="quantity-input border-l-0 border-r-0" 
              readOnly
              data-testid="input-quantity"
            />
            <button 
              className="quantity-button rounded-r-md" 
              onClick={increaseQuantity}
              data-testid="button-increase-quantity"
            >
              +
            </button>
          </div>
        </div>

        <button 
          className="w-full bg-primary text-primary-foreground py-4 rounded-lg font-bold text-lg hover:opacity-90 transition-opacity flex items-center justify-center gap-2 disabled:opacity-50" 
          onClick={handleAddToCart}
          disabled={isAdding}
          data-testid="button-add-to-cart"
        >
          <ShoppingCart className="w-5 h-5" />
          {isAdding ? "Adicionando..." : "Adicionar ao Carrinho"}
        </button>
      </div>
    </div>
  );
}

